(function() {
  var fnAddToken;

  fnAddToken = function(url, token) {
    if (/.*\?.*/.test(url)) {
      return url + "&token=" + token;
    } else {
      return url + "?token=" + token;
    }
  };

  $(function() {
    var block_page, finish_export, mytoken;
    $.removeCookie('export_token', {
      path: '/'
    });
    mytoken = 0;
    finish_export = function() {
      window.clearInterval(fileDownloadCheckTimer);
      $.removeCookie('export_token', {
        path: '/'
      });
      return $.unblockUI();
    };
    $('#icon_pdf').click(function() {
      var lien;
      mytoken = new Date().getTime().toString();
      lien = $('#icon_pdf').attr('href');
      $('#icon_pdf').attr('href', fnAddToken(lien, mytoken));
      return block_page();
    });
    $('#icon_csv').click(function() {
      var lien;
      mytoken = new Date().getTime().toString();
      lien = $('#icon_csv').attr('href');
      $('#icon_csv').attr('href', fnAddToken(lien, mytoken));
      return block_page();
    });
    $('#icon_xls').click(function() {
      var lien;
      mytoken = new Date().getTime().toString();
      lien = $('#icon_xls').attr('href');
      $('#icon_xls').attr('href', fnAddToken(lien, mytoken));
      return block_page();
    });
    return block_page = function() {
      var fileDownloadCheckTimer;
      $.blockUI({
        message: '<h1>Juste un instant...</h1>'
      });
      return fileDownloadCheckTimer = window.setInterval(function() {
        var cookieValue;
        cookieValue = $.cookie('export_token');
        if (cookieValue === mytoken) {
          return finish_export();
        }
      }, 1000);
    };
  });

}).call(this);
