// la mise en forme des champs de saise des montants (debit et credit) est assure
// par la classe .decimal et les fonctions qui sont dfinies dans application.js
// $f_empty pour vider un champ quand on entre et qu'il est  zero
// $f_two_decimals pour mettre deux dcimales quand on sort du champ et le
// remettre  zero si on a rien rentr.

// On cache tous les sigles plus sauf celui de la dernire ligne
// puis on relie le click de cette image au lien qui ajoute la ligne
//
function $deal_icon_plus(e) {
  var bouton = $(this); //on mmorise le bouton cliqu pour pouvoir 
  // l'effacer en cas de succs
  // on trouve quel est le dernier numro de ligne existant dans la page
  var lastid = $('.writing_line_form').filter(":last").
          find('input.numeric.decimal.debit').attr('id').split('_')[4];
  // enfin, on prpare l'url demande. Il faut remplacer /edit ou /new
  // par '/add_line?num_line=' + lastid
  var chemin = window.location.pathname;
  if (chemin.match('new')) {
    chemin = chemin.replace('/new', '/add_line?num_line=' + lastid);
  } else {
    chemin = chemin.replace('/edit', '/add_line?num_line=' + lastid);
  }


  $.ajax({
    url: chemin,
    type: 'post',
    success: function(data, textStatus, jqXHR) {
      bouton.hide(); // on masque l'icone + sur laquelle on vient de cliquer
 // comme on a rattach les vnements par dlagation au dbut de l'action
 // on n'a pas besoin de les rattacher de nouveau. Voir la doc sur on().
    }
  });


}

// renvoie le total correspondant au selector mais seulement pour les lignes
// visibles.
// Cette fonction est utilise par $balance qui fait appel  $total_sel('.credit')
// et  $total_sel('.debit')
function $total_sel(selector) {
  var t = 0;
  $(selector).each(function(index) {
    if ($(this).closest('.writing_line_form').is(':visible')) {
      t = t + parseFloat($(this).val(), 10);
    }
  });
  return t;
}

function $balance() {
  return ($total_sel('.credit') - $total_sel('.debit')).toFixed(2);
}



// fait la somme des dbit et la somme des credits et retourne un booleen indiquant l'galit
function $balanced() {
  if ($total_sel('.debit') === $total_sel('.credit')) {
    return true;
  } else {
    return false;
  }
}

// retourne le nombre de lignes qui ait soit dbit soit credit de rempli
function $nb_lines() {
  var i = 0;
  $('.compta_writings .writing_line_form').each(function(index) {
    if (($(this).find('.debit:first').val() !== '0.00') || ($(this).find('.credit:first').val() !== '0.00')) {
      i = i + 1;
    }

  });
  return i;
}

// vrifie si la ligne peut tre enregistre et affiche le bouton si oui
// deux conditions sont testes : au moins une ligne remplie et une criture quilibre
// Affiche le solde dans la zone h3
function $check_submit() {
  var bal = $balance(), nb_lines = $nb_lines();
  if ((bal === '0.00') && (nb_lines > 1)) {
    $('input.btn').show();
  } else {
    $('input.btn').hide();
  }
  $('h3 #sold_value').text(bal);
}

// retire une compta_line du formulaire ou plutt la cache
function $remove_writing_line_form() {
  console.log('bonjour');
  var wlf = $(this).closest(".writing_line_form");
  // marque le champ cach pour la suppression   
  $(this).prev("input[type=hidden]").val("1");
  wlf.hide();
  // on montre le bouton plus de la dernire ligne visible
  $('.compta_writings .writing_line_form:visible:last a.add_form_line').show();
  // on vrifie que l'on peut afficher le bouton submit
  $check_submit();
}




// il ne peut y avoir une ligne ayant  la fois dbit et crdit de rempli
// donc on fait une fonction qui lorsqu'on quitte le champ remet  zero son
// correspondant si le montant est diffrent de zero
function $zero_field() {
// on ne fait rien si le champ est  zro ou s'il est vide'
  if (($(this).val() === '0.00') || ($(this).val() === '')) {
    return;
  }

//  if (parseFloat($(this).val(), 10) == 0.00) {
//    return
//  }
  // sinon on cherche qui on est (soit credit soit dbit)
  // et on remet  zero l'autre champ
  if ($(this).hasClass('debit') === true) {
    $(this).closest('.writing_line_form').find('.credit').val('0.00');
  } else {
    $(this).closest('.writing_line_form').find('.debit').val('0.00');
  }
}


// Objectif : avoir une icone plus  ct des forms de compta_line pour
