(function() {
  var check_ibel_complete, refill_payment_mode_values, replace_collection, set_date_limits;

  set_date_limits = function() {
    var datemax, datemin;
    datemin = $('tbody').attr('data-mindate');
    datemax = $('tbody').attr('data-maxdate');
    return $.datepicker.setDefaults({
      dateFormat: 'dd/mm/yy',
      minDate: datemin,
      maxDate: datemax
    });
  };

  check_ibel_complete = function(row) {
    var complete, t, u, v;
    t = row.find('span[data-bip-attribute="destination_id"]');
    u = row.find('span[data-bip-attribute="nature_id"]');
    v = row.find('span[data-bip-attribute="payment_mode"]');
    complete = !(/-/.test(t.text()) || /-/.test(u.text()) || /-/.test(v.text()));
    if (row.find('td.cat span').text().trim() === 'T') {
      complete = !(/-/.test(v.text()));
    }
    if (complete) {
      return row.find('a.ibel_write').show();
    } else {
      return row.find('a.ibel_write').hide();
    }
  };

  replace_collection = function(source, dest) {
    dest.data('bestInPlaceEditor').values = JSON.parse(source.attr('data-collection'));
    return dest.text('-');
  };

  refill_payment_mode_values = function(field) {
    var pm;
    pm = field.parents('tr').find('td.payment_mode span.best_in_place');
    if (field.text() !== pm.attr('data-cat')) {
      pm.attr('data-cat', field.text());
      if (field.text() === 'T') {
        replace_collection($('div#transfer'), pm);
        field.parents('tr').find('td.destination span.best_in_place').hide();
        return field.parents('tr').find('td.nature span.best_in_place').hide();
      } else {
        replace_collection($('div#depenses'), pm);
        field.parents('tr').find('td.destination span.best_in_place').show();
        return field.parents('tr').find('td.nature span.best_in_place').show();
      }
    }
  };

  $(function() {
    set_date_limits();
    $(".public_imported_bels .best_in_place").best_in_place();
    $('.public_imported_bels tr.importable').each(function() {
      if ($(this).find('td.cat').text().trim() === 'T') {
        $(this).find('td.destination span').hide();
        return $(this).find('td.nature span').hide();
      }
    });
    return $('.public_imported_bels .best_in_place').bind("ajax:success", function() {
      if ($(this).attr('data-bip-attribute') === 'cat') {
        refill_payment_mode_values($(this));
      }
      return check_ibel_complete($(this).parents('tr'));
    });
  });

}).call(this);

