// PARTIE POUR LE FORMULAIRE
//
//
//  des fonctions $f_empty et $f_two_decimals sont dfinies dans application.js
//
// .decimal (toujours dans application js) permet de grer l'entre et la sortie des
// champs de classe dcimal
//
//
// gre l'affichage des champs banque et caisse;
// La fonction est utilise pour les in_out_writings que le formulaire soit appel
// par le controller in_out_writings ou mask_writings. 
// 
//
// evt transmet en original_data la valeur initiale de l'criture si on est en modification
// d'criture. Sinon transmet les valeurs par dfaut, donc la premire banque de la liste
// et la premire caisse de la liste.
//
function $f_td_bank_cash(evt) {
    var caisses, banques, income_outcome, payment_mode, encaissement, bank_value, cash_value;
    caisses = $('optgroup[label="Caisses"] option');
    banques = $('optgroup[label="Banques"] option');
    bank_value = evt.data.bank_value;
    cash_value = evt.data.cash_value;
    // pour n'afficher Chque  l'encaissement que pour les livres de recettes
    if ($('.income_book#entry_lines').length > 0) {
        income_outcome = true;
        encaissement = $('optgroup[label="Chques  l\'encaissement"] option');
    } else {
        income_outcome = false;
    }

    payment_mode = $(this).val();


    // s'il y a plus d'un lment dans td_bank et si le mode de payemnt 
    // est autre que Espces alors afficher td_bank et disable td_cash

    switch (payment_mode) {
    case 'Espces':
        banques.attr('disabled', 'disabled');
        $('#td_check_number').hide();
        caisses.attr('disabled', false);
        banques.attr('selected', false);
        // on masque la zone chque  l'encaissement si elle existe
        if (income_outcome) {
            encaissement.attr('selected', false).attr('disabled', 'disabled');
        }
        cash_value.attr('selected', 'selected');
        break;
    case 'Chque':
        caisses.attr('disabled', 'disabled');
        caisses.attr('selected', false);
        if (income_outcome) {
            $('#td_check_number').hide(); // masquage du champ pour  saisir le n de chque de la dpenses
            banques.attr('disabled', 'disabled');
            banques.attr('selected', false);
            encaissement.attr('disabled', false);
            encaissement.first().attr('selected', 'selected');
        } else {
            $('#td_check_number').show(); // affichage du champ pour  saisir le n de chque de la dpenses
            banques.attr('disabled', false);
            if (banques.size() >= 1) {
                bank_value.attr('selected', 'selected');
            }
        }
        break;
        // autres cas : une recette en banque qui n'est pas un chque
    default:
        $('#td_check_number').hide(); // masquage du champ n de chque
        caisses.attr('disabled', 'disabled'); // dsactivation des caisses
        caisses.attr('selected', false);
        banques.attr('disabled', false);
        if (banques.size() >= 1) {
            // on ne slectionne une banque que si elle ne l'est pas dja par
            // l'enregistrement
            bank_value.attr('selected', 'selected');

        }
        if (income_outcome) {
            encaissement.attr('selected', false);
            encaissement.attr('disabled', 'disabled');
        }
    }
}

