// petite fonction helper pour transformer des strings en float.
// si ce n'est pas un nombre, transforme NaN en null. Ceci est
// ncessaire pour ne pas gnrer d'erreur lorsqu'une donne
// est absente. Cas d'un exercice incomplet par exemple.
function s_to_f(element) {
    var e = parseFloat(element);
    if (isNaN(e)) {
        e = null;
    }
    return e;
}

// cette fonction rcupre les informations caches qui sont inclus dans un DOM
//  partir de l'id et des classes legend, ticks, month_years et series'
function recup_graph_datas(element) {
    var complete_id, type, id, legend, ticks,  s = [], label = [], link = [], i = 0;
    $(element).each(function () { // pour chacun des graphiques mensuels (chacun des livres plus result)
        // on construit les variables qui seront utilises par jqplot
        complete_id = this.id;
        type = $(this).attr('class').split('_')[0];
        id = this.id.match(/\d+$/)[0]; // on rcupre l'id et comme match retourne un array on prend le premier'
        legend = $(this).find('.legend').text().split(';'); // la lgende
        ticks = $(this).find('.ticks').text().split(';'); // les mois
        // et on les remplit par une boucle qui prend la dimension de lgende pour construire
        for (i = 0; i < legend.length; i += 1) {
            label[i] = {
                label: legend[i] // la table des lgendes
            };
            s[i] = $(this).find('.series_' + i).text().split(';').map(s_to_f); // et chaque srie de donnes en float
            link[i] = $(this).find('.month_years_' + i).text().split(';'); // et les mm-yyyy correspondant 
        }
    });
    // on retourne maintenant l'objet ainsi construit reprenant la totalit des infos
    return {
        dcomplete_id: complete_id,
        did: id,
        dlegend: legend,
        dticks: ticks,
        dseries: s,
        dlinks: link,
        dlabel: label,
        dtype: type
    };
}




// prend les donnes d'un graphe (fournies par un appel  recup_graph_datas)
// et un type de graphe ('normal' ou 'bar') et construit les options qui seront
// ncessaires pour jqplot (legende, sries, ticks,...)
function options_for_graph(all_datas) {
    var options = {
        seriesDefaults: {
            pointLabels: {
                show: false
            },
            lineWidth: 2,
            markerOptions: {
                size: 3,
                style: "circle"
            }
        },
        series: all_datas.dlabel,
        highlighter: {
            sizeAdjust: 5,
            tooltipLocation: 'n',
            tooltipAxes: 'y',
            useAxesFormatters: true
        },
        legend: {
            renderer: $.jqplot.EnhancedLegendRenderer,
            //  numberRows: 1,
            //  numberColumns: 2,
            show: true,
            placement: 'insideGrid',
            location: 'ne',
            fontSize: '8pt',
            textColor: 'blue',
            rendererOptions: {
                numberRows: 1,
                numberColumns: all_datas.dlegend.length
            }
        },
        cursor: {
            show: false,
            zoom: false,
            looseZoom: false,
            showTooltip: false
        },
        axes: {
            // Use a category axis on the x axis and use our custom ticks.
            xaxis: {
                renderer: $.jqplot.CategoryAxisRenderer,
                ticks: all_datas.dticks,
                tickRenderer: $.jqplot.CanvasAxisTickRenderer,
                tickOptions: {
                    angle: 0,
                    fontSize: '8pt',
                    showMark: true,
                    showGridline: false
                }
            },

            // Pad the y axis just a little so bars can get close to, but
            // not touch, the grid boundaries.  1.2 is the default padding.
            yaxis: {
                pad: 1.05,
                tickOptions: {
                    formatString: "\u20ac %d"
                }
            }
        }
    }; // fin des default options pour un graphe normal

    // ici on surcharge seriesDefaults pour les graphes de type 'bar'
    if (all_datas.dtype === 'bar') {
        // le renderer bar et ces options
        options.seriesDefaults = {
            renderer: $.jqplot.BarRenderer,
            pointLabels: {
                show: false
            },
            rendererOptions: {
                fillToZero: true,
                barPadding: 0,      // number of pixels between adjacent bars in the same
                // group (same category or bin).
                barMargin: 5,      // number of pixels between adjacent groups of bars.
                barDirection: 'vertical', // vertical or horizontal.
                barWidth: 10,     // width of the bars.  null to calculate automatically.
                shadowOffset: 0,    // offset from the bar edge to stroke the shadow.
                shadowDepth: 0,     // nuber of strokes to make for the shadow.
                shadowAlpha: 0 // transparency of the shadow.
            //, location: 'e', edgeTolerance: -15 }
            }
        };
    }
    return options;
}

// fonction qui associe les lments du graphe  l'affichage du book_extract correspondant'
function bind_bars(all_datas) {
    $('#chart_' + all_datas.dcomplete_id).bind('jqplotDataClick',
        function (ev, seriesIndex, pointIndex, data) {
            var mois = '', la = [], an = '', lien = '';
            lien = all_datas.dlinks[seriesIndex][pointIndex]; // lien = mm-yyyy  ce stade
            la = lien.split('-');
            mois = la[0]; an = la[1];
            window.location = ("/books/" + all_datas.did + "/in_out_writings?an=" + an + "&mois=" + mois);
        });
}

/* La fonction bind_cashes a pour effet de permettre de cliquer sur les points
 * de la courbe de trsorerie des caisses pour afficher les mouvements du mois correspondants
 * 
 *  On n'a pas la mme chose pour la banque car il n'y a pas de livre de banque  proprement parler
 *  mais plutt des extraits bancaires.  
 *  */
function bind_cashes(all_datas) {
    $('#chart_' + all_datas.dcomplete_id).bind('jqplotDataClick',
        function (ev, seriesIndex, pointIndex, data) {
            var mois = '', la = [], an = '', lien = '';
            lien = all_datas.dlinks[seriesIndex][pointIndex]; // lien = mm-yyyy  ce stade
            la = lien.split('-');
            mois = la[0]; an = la[1];
            window.location = ("/cashes/" + all_datas.did + "/cash_lines?an=" + an + "&mois=" + mois);
        });
}


// la fonctio monthly_graphic est appele par des lments du class
// .bar_... ou .line... et fait le travail de trac de graphique et
// ventuellement de liens avec les vnements clics sur les barres pour
// les objets de type book (sauf book 0 qui est le rsultat)
function monthly_graphic(element) {
    var all_datas, options;
    // pour chacun des graphiques mensuels
    all_datas = recup_graph_datas(element);  // on rcupre les donnes  partir de span hidden
    options = options_for_graph(all_datas); // on construit les options
    $.jqplot('chart_' + all_datas.dcomplete_id, all_datas.dseries, options); // et on trace dans l'id chart_cash_id_1 ou chart_book_id_2'
    // puis on relie les colonnes des graphes  l'affichage du livre correspondant lorsqu'il s'agit d'un book
    // et lorsque ce n'est pas le book 0 qui est celui du rsultats
    if (all_datas.dcomplete_id.match(/book/) && all_datas.did > 0) {
        bind_bars(all_datas); // fait le lien avec les barres
    }
    if (all_datas.dcomplete_id.match(/cash/)) {
        bind_cashes(all_datas); // fait le lien avec les barres
    }
    
}

