// fonction pour transformer une chaine avec virgule en float
// la fonction retire les espaces et remplace la virgule par le point dcimal
// avant d'appeler Number, la fonction native de js.
function stringToFloat(jcdata) {

    if (jcdata === undefined) {
        return 0.0;
    }
    var d = String(jcdata).replace(/,/, '.');
    d = d.replace(/\s/g, '');
    if (isNaN(d)) {
        return 0.0;
    } else {
        return Number(d);
    }
}

// prend un nombre et en fait une chane avec deux dcimales et 
// une virgule comme sparateur dcimal
function $f_numberWithPrecision(number) {
    var part1, part2, parts, rgx = /(\d+)(\d{3})/;
    if (number === undefined) {
        return '-';
    }
    number =  number.toFixed(2); // on garde deux dcimales
    if (isNaN(number)) {
        return '-';
    } else {
        parts = String(number).split('.');
        part1 = parts[0];
        part2 = parts.length > 1 ? ',' + parts[1] : '';
        while (rgx.test(part1)) {
            part1 = part1.replace(rgx, '$1' + ' ' + '$2');
        }
        return part1 + part2;
    }
}

// fonction permettant de mettre deux dcimales  un nombre
// appel lorsque l'utilisateur sort d'un champ pour remettre en forme sa saisir
// Par exemple 32 devient 32.00 ou 32.5 devient 32.50
function $f_two_decimals() {
    var number = stringToFloat(this.value);
    if (isNaN(number)) {
        this.value = '0.00';
    } else {
        this.value = number.toFixed(2);
    }
}

// srie de fonction qui prpare les champs dbit et crdit pour la saisie
// quand on entre dans un champ qui est  0, on le vide
function $f_empty() {
    if (this.value === '0.00') {
        this.value = '';
    }
}


jQuery(function () {
    $('#main-zone').on('focus', '.decimal', $f_empty); //vide le champ s'il est  zero (pour faciliter la saisie)
    $('#main-zone').on('blur', '.decimal', $f_two_decimals);
});

