// fnMoveRows est la fonction appele aprs la rponse ok de
// reorder du controller pour renumroter l'information de position des lignes
// en effet, la mise  jour de l'affichage est faite par javascript et non par un
// render du controller
function fnMoveRows(tbody, from, to) {
    var iFrom = parseInt(from), iTo = parseInt(to), pos;
    // par exemple, je passe la ligne 2  la ligne 6,
    if (iTo > iFrom) {
        tbody.find('tr').each(function (index) {
            pos = parseInt($(this).attr('data-position'));
            // et la ligne 2 devient la ligne 6
            if (pos === iFrom) {
                fnChangeValue($(this), iTo.toString());
            }
            // les lignes 3  6 perdent 1 cran
            if (pos > iFrom && pos <= iTo) {
                fnChangeValue($(this), (pos - 1).toString());
            }
        });
    }
    //dans l'autre sens, je passe de la ligne 6  la ligne 2'
    // donc iFrom = 6 et iTo = 2
    if (iTo < iFrom) {
        tbody.find('tr').each(function (index) {
            pos = parseInt($(this).attr('data-position'));
            // et la ligne 6 devient la ligne 2
            if (pos === iFrom) {
                fnChangeValue($(this), iTo.toString());
            }

            // les lignes 2  5 gagnent 1 cran
            if (pos >= iTo && pos < iFrom) {
                fnChangeValue($(this), (parseInt(pos) + 1).toString());
            }
        });
    }
}

/* Ces deux fonctions ont pour objet de bloquer la page pour une action de tlchargement de 
 * fichier. 
 * 
 * Il faut modifier le formulaire utilis pour introduire un champ cach avec l'id
 * 'download_token_value_id'. La fonction cre un token avec la date et l'heure puis 
 * complte ce champ cach et l'envoie au serveur.
 *
 * Il met en place un timer pour vrifier l'arrive du cookie
 * 
 * Le serveur, dans sa rponse doit donc renvoyer un cookie avec cette valeur.
 * Que la rponse soit positive ou pas, ne serait-ce que pour arrter le timer et 
 * le blocage.
 *
 * Lorsque le cookie arrive, le timer appelle finishDownload qui retire le cookie et
 * dbloque la page.
 */
var fileDownloadCheckTimer;
function blockUIForDownload() {
    window.clearInterval(fileDownloadCheckTimer);
    $.removeCookie('download_file_token', { path: '/' }); //remove the cookie
    var token = new Date().getTime(); //use the current timestamp as the token value

    $('#download_token_value_id').val(token);
    $('.inner-champ').block({ message: '<h1>Juste un instant...</h1>' });
    // $.blockUI();
    fileDownloadCheckTimer = window.setInterval(function () {
        var cookieValue = $.cookie('download_file_token');
        if (cookieValue === token) {
            finishDownload();
        }
    }, 1000);
}


function finishDownload() {
    window.clearInterval(fileDownloadCheckTimer);
    $.removeCookie('download_file_token', { path: '/' }); //remove the cookie
    $('.inner-champ').unblock();
}





// Cette fonction permet de rendre une table sortable en indiquant une action
//  appeler par la fonction ajax
// Exemple fnTableSortable($('.admin_natures #recettes'), '/reorder');
// rend la table #recettes sortable sur elle-mme en appelant l'action
// reorder du controller qui a affich la vue (ici natures_controller#index)
// donc l'url sera par exemple periods/2/natures
// et l'url appele par la fonction ajax sera periods/2/natures/reorder'
//
//L'attribut data-position de chaque ligne donne sa ligne d'origine
//Il faut donc trouver la position du drop
//puis envoyer au controller une action avec les paramtres id, fromPosition et 
//toPosition. 
//
function fnTableSortable(table, action) {
    $(table).sortable({
        connectWith: table.val('id'),
        items: "tr",
        //callback utilise pour le changement d'emplacement  l'intrieur de la table
        // des natures.
        update: function (event, ui) {
            var tbody = table,
                id = ui.item.context.id,
                from = $("#" + id).attr('data-position'),
                to = -1;
            // les id des lignes sont constitues uniquement de l'id de la Nature

            // la logique est la suivante : data-position donne la position initiale de la ligne
            // aprs un dplacement data-position est du coup le numro de ligne d'origine

            // il faut donc trouver  quelle place se trouve le drop
            // chercher quel est le rang en balayant les lignes

            table.find('tr').each(function (index) {
                if ($(this).attr('id') === id) {
                    to = index;
                }

            });

            $.ajax({
              // l'action actuelle avec en plus l'action transmise en paramtre
              // cel permet pour une vue index de l'appeler avec /reorder
              // en supposant que l'action dans le controller est reorder
              // Si on est dans une action prcise par exemple pointage,
              // cela permet de transmettre _reorder comme paramtre action
              // ce qui appelera l'action pointage_reorder dans le controller'
                url: window.location.pathname + action,
                type: 'post',
                data: {
                    id: id,
                    fromPosition: from,
                    toPosition: to
                },
                // puis on fait la mise  jour des donnes de la table
                success: function () {
                    fnMoveRows(tbody, from, to);
                },
                // ou inversement on annule si erreur
                error: function (jqXHR) {
                    fnCancelSorting(tbody, jqXHR.statusText);
                }
            });

        }
    });
}
